        //全选与反选
		function check(obj){
			var f = $(obj)[0].checked;
			$(".checkbox").each(function(){
				$(this).attr("checked",f);
				if($(this).attr('checked')){
					$(this).siblings(".lbl").addClass("checkedbox");
				}else{
					$(this).siblings(".lbl").removeClass("checkedbox");
				}
			});
		}
		// title 气泡
	  $(function () { $("[data-toggle='tooltip']").tooltip(); });
	  function Print(obj,text){
	
	   $("body").append("<i class='group-i'>"+text+"</i>");
	   $("."+obj+"").focus();
		var _left = $("."+obj+"").offset().left;
		var _top = $("."+obj+"").offset().top-33;
		$(".group-i").css({
			   "left":""+_left+"px",
			   "top":""+_top+"px",
			}); 
		setTimeout(function(){$("body").find(".group-i").remove();},3000)	
  }
  function wait(){ 
    $("body").append("<div class='ibox-print'><div class='ibox-wait'><div class='sk-spinner sk-spinner-chasing-dots'><div class='sk-dot1'></div><div class='sk-dot2'></div></div></div></div>");
  }